import { useState, useEffect } from "react";
// agar login form bo‘lsa, kerakli importlar
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-[#208a00]"></div>
      </div>
    );
  }

  return (
    <div className="bg-[#F3F4F6] min-h-screen flex items-center justify-center px-4">
      <div className="w-full max-w-2xl bg-white rounded-xl shadow-md p-4">
        {/* Iframe orqali Visme formani joylashtirish */}
        <iframe
          src="https://forms.visme.co/formsPlayer/y71dy3mj-untitled-project"
          title="Event Registration Form"
          style={{
            width: "100%",
            height: "100vh",
            border: "none",
          }}
        ></iframe>
      </div>
    </div>
  );
};

export default LoginPage;
