import { useState, useEffect } from "react";
import { Player } from "@lottiefiles/react-lottie-player";
import Animated from "../assets/Animated.json";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

const LoginPage = () => {
  const [loading, setLoading] = useState(true);
  const [password, setpassword] = useState("");
  const [phone, setPhone] = useState("");
  const [passwordError, setpasswordError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [touched, setTouched] = useState({ password: false, phone: false });

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  const validatepassword = (val) => {
    if (!val) return "password is required";
    if (val.length < 3) return "At least 3 characters";
    return "";
  };

  const validatePhone = (val) => {
    if (!val || val.length < 9) return "Invalid phone number";
    return "";
  };

  const handleContinue = () => {
    setpasswordError(validatepassword(password));
    setPhoneError(validatePhone(phone));
    setTouched({ password: true, phone: true });
  };

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center bg-[#F3F4F6]">
        <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-[#208a00]"></div>
      </div>
    );
  }

  return (
    <div className="bg-[#F3F4F6] min-h-screen flex items-center justify-center">
      <div className="containerr flex md:flex-row items-center justify-between gap-10 w-full">
        {/* Animation */}
        <div className="">
          <Player
            autoplay
            loop
            src={Animated}
            style={{ height: "550px", width: "550px" }}
          />
        </div>

        {/* Login form */}
        <div className="flex justify-center h-screen fixed 2xl:w-[800px] w-1/2 right-0">
          <div className="w-full p-1 rounded-xl">
            <div className="bg-white shadow-lg rounded-xl p-14 w-full h-full flex flex-col justify-center">
              <h2 className="text-2xl font-semibold text-center mb-4">Login</h2>

              {/* Phone */}
              <PhoneInput
                value={phone}
                onChange={(val) => {
                  setPhone(val);
                  setPhoneError(validatePhone(val));
                  setTouched((prev) => ({ ...prev, phone: true }));
                }}
                onFocus={() => {
                  if (!phone) {
                    setPhone("+998");
                  }
                }}
                placeholder="+998 99-999-99-99"
                inputProps={{
                  name: "phone",
                  required: true,
                  className: `border-2 outline-none w-full px-4 py-3 my-2 rounded-lg ${
                    phoneError && touched.phone
                      ? "border-[#F97316]"
                      : "border-[#D1D5DB] focus:ring-[#208a00]"
                  }`,
                }}
              />
              {phoneError && touched.phone && (
                <p className="text-[#F97316] text-sm">{phoneError}</p>
              )}
              {/* password */}
              <input
                type="text"
                placeholder="Password"
                value={password}
                onChange={(e) => {
                  const val = e.target.value;
                  setpassword(val);
                  setpasswordError(validatepassword(val));
                  setTouched((prev) => ({ ...prev, password: true }));
                }}
                className={`border-2 outline-none px-4 py-3 my-2 rounded-lg ${
                  passwordError && touched.password
                    ? "border-[#F97316]"
                    : "border-[#D1D5DB] focus:ring-[#208a00]"
                }`}
              />
              {passwordError && touched.password && (
                <p className="text-[#F97316] text-sm mb-1">{passwordError}</p>
              )}

              {/* Button */}
              <button
                onClick={handleContinue}
                className="w-full mt-5 text-white py-2 rounded-lg font-semibold transition cursor-pointer
                bg-gradient-to-r btn-gradient"
              >
                Continue
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
