import { useState, useEffect } from "react";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import axios from "axios";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const [loading, setLoading] = useState(true);
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [touched, setTouched] = useState({ phone: false, password: false });
  const [errorMessage, setErrorMessage] = useState("");
  const [step, setStep] = useState(0); // animatsiya bosqichlari

  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  const validatePhone = (value) => {
    if (!value || value.length < 12) {
      return "Telefon raqam to‘liq kiritilishi kerak";
    }
    return "";
  };

  const validatePassword = (value) => {
    if (!value || value.length < 4) {
      return "Parol 4 ta belgidan kam bo‘lmasligi kerak";
    }
    return "";
  };

  const handleContinue = async () => {
    try {
      const res = await axios.post(
        `http://167.86.121.42:8080/auth/login?phone=${phone}&password=${password}`
      );

      if (res.data.success) {
        const role = res.data.message;
        if (role === "ADMIN") navigate("/admin-dashboard");
        else if (role === "TEACHER") navigate("/teacher-dashboard");
        else if (role === "USER" || role === "PARENTS")
          navigate("/user-dashboard");
        else setErrorMessage("⚠️ Noma'lum rol qaytdi.");
      } else {
        setErrorMessage("❌ Login ma'lumotlari noto‘g‘ri.");
      }
    } catch (err) {
      console.error(err);
      setErrorMessage("❌ Serverga ulanishda xatolik.");
    }
  };

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-[#208a00]"></div>
      </div>
    );
  }

  return (
    <div className="bg-[#F3F4F6] min-h-screen flex items-center justify-center px-4 relative overflow-hidden">
      {/* Odam */}
      <motion.img
        src="/person.png"
        alt="Odam"
        initial={{ x: -300, opacity: 0 }}
        animate={
          step === 0
            ? { x: -100, opacity: 1 }
            : step === 1
            ? { x: 0 }
            : step === 2
            ? { x: -150, scale: 0.9 }
            : { x: -50, y: 50 }
        }
        transition={{ duration: 1 }}
        className="absolute bottom-20 left-10 w-32 z-20"
      />

      {/* Sumka */}
      <motion.img
        src="/bag.png"
        alt="Sumka"
        initial={{ scale: 1 }}
        animate={step === 1 ? { scale: 1.1 } : { scale: 1 }}
        transition={{ duration: 0.5 }}
        onClick={() => setStep(1)}
        className="absolute bottom-20 left-1/2 transform -translate-x-1/2 w-24 cursor-pointer z-10"
      />

      {/* LOGIN CARD */}
      {step >= 1 && (
        <motion.div
          initial={{ y: 200, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1 }}
          className="bg-[#fff] w-full max-w-sm p-1 rounded-xl shadow-md relative z-30"
        >
          <div className="bg-transparent rounded-xl p-6 w-full flex flex-col">
            <h2 className="text-2xl font-semibold text-center mb-4">Login</h2>

            <PhoneInput
              value={phone}
              onChange={(val) => {
                setPhone(val);
                setPhoneError(validatePhone(val));
                setTouched((prev) => ({ ...prev, phone: true }));
              }}
              onFocus={() => {
                if (!phone) setPhone("+998");
              }}
              placeholder="+998 99-999-99-99"
              inputProps={{
                name: "phone",
                required: true,
                className: `border-[1.8px] border-[#00000049] outline-none px-4 py-3 my-2 rounded-lg w-full ${
                  phoneError && touched.phone
                    ? "border-red-600"
                    : "border-[#D1D5DB] focus:ring-[#208a00]"
                }`,
              }}
            />

            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => {
                const val = e.target.value;
                setPassword(val);
                setPasswordError(validatePassword(val));
                setTouched((prev) => ({ ...prev, password: true }));
              }}
              className={`border-[1.8px] border-[#00000049] outline-none px-4 py-3 my-2 rounded-lg ${
                passwordError && touched.password
                  ? "border-red-600"
                  : "border-[#D1D5DB]"
              }`}
            />

            <button
              onClick={handleContinue}
              className="w-full mt-5 btn-gradient text-white py-2 rounded-lg font-semibold transition"
            >
              Continue
            </button>
            {errorMessage && (
              <p className="text-red-600 text-center mt-3">{errorMessage}</p>
            )}
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default LoginPage;
